package controlstatements;

public class DowhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i=1;
		
		do {
			
			System.out.println(i);
			i++;		
		}
		while(i<=100);

		System.out.println("===========================================");
		
		int a=100;
		do {
			System.out.println(a);
			a--;
			
		}
		while(a>=1);
		
	}

}
