package controlstatements;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
		for(int i=0;i<=100;i++) {
			
			
			System.out.println(i);
		}
		
		System.out.println("--------------------------------------------------");
		
		for(int j=100;j>=0;j--) {
			
			System.out.println(j);
			
		}
	}

}
