package controlstatements;

public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		int a=1;
	   
		while(a<=100) {
			
			System.out.println(a);
			a++;
			break;
		}
		
		System.out.println("===========================================");
		
		int b=100;
		while(b>=1) {
			
			System.out.println(b);
			b--;
		}
		
	}

}
